        if (desktopNav) {
            mobileNav = document.createElement('div');
            mobileNav.className = 'mobile-nav';
            var clonedNav = desktopNav.cloneNode(true);
            mobileNav.appendChild(clonedNav);
            document.querySelector('.main-header').appendChild(mobileNav);
            console.log('Mobile nav created');
        }
    }

    // Simple hamburger toggle
    if (hamburger && mobileNav) {
        console.log('Setting up hamburger click...');

        hamburger.addEventListener('click', function(e) {
            console.log('Hamburger clicked - toggling menu');
            e.stopPropagation();
            mobileNav.classList.toggle('show');
            this.classList.toggle('active');
        });

        // Close menu when clicking regular links (not dropdowns)
        var regularLinks = mobileNav.querySelectorAll('a:not(.dropbtn)');
        for (var i = 0; i < regularLinks.length; i++) {
            regularLinks[i].addEventListener('click', function() {
                console.log('Regular link clicked - closing menu');
                if (!this.classList.contains('dropbtn')) {
                    mobileNav.classList.remove('show');
                    hamburger.classList.remove('active');
                }
            });
        }

        // Close menu when clicking outside
        document.addEventListener('click', function(e) {
            if (mobileNav.classList.contains('show') &&
                !e.target.closest('.mobile-nav') && 
                !e.target.closest('.hamburger-menu')) {
                console.log('Clicked outside - closing menu');
                mobileNav.classList.remove('show');
                hamburger.classList.remove('active');
            }
        });

        console.log('Hamburger menu setup complete');
    } else {
        console.log('Hamburger or mobile nav not found');
    }
});
//

//
// ===== DROPDOWN MENU =====
document.addEventListener('DOMContentLoaded', function() {
    console.log('Initializing dropdown menus...');

    // Wait a bit to ensure mobile nav is created
    setTimeout(function() {
        var dropdownToggles = document.querySelectorAll('.dropbtn');
        console.log('Dropdown toggles found:', dropdownToggles.length);

        for (var i = 0; i < dropdownToggles.length; i++) {
            dropdownToggles[i].addEventListener('click', function(e) {
                console.log('Dropdown clicked');
                e.preventDefault();
                e.stopPropagation();

                var dropdown = this.closest('.dropdown, .has-dropdown');
                if (dropdown) {
                    var isActive = dropdown.classList.contains('active');

                    // Close all other dropdowns
                    var allDropdowns = document.querySelectorAll('.dropdown.active, .has-dropdown.active');
                    for (var j = 0; j < allDropdowns.length; j++) {
                        if (allDropdowns[j] !== dropdown) {
                            allDropdowns[j].classList.remove('active');
                        }
                    }

                    // Toggle current dropdown
                    if (!isActive) {
                        dropdown.classList.add('active');
                    } else {
                        dropdown.classList.remove('active');
                    }
                }
            });
        }

        // Close dropdowns when clicking outside
        document.addEventListener('click', function(e) {
            if (!e.target.closest('.dropdown') && !e.target.closest('.has-dropdown')) {
                var openDropdowns = document.querySelectorAll('.dropdown.active, .has-dropdown.active');
                for (var i = 0; i < openDropdowns.length; i++) {
                    openDropdowns[i].classList.remove('active');
                }
            }
        });

    }, 100);
});
